    setName(nombre.value);
  }

  return (
    <div>
      {/*localStorage.setItem('name', JSON.stringify(this.state))*/}
      <p>Esooo {name}!!</p>
      <input id="nombre" type="text"></input> <button type="button" onClick={handleClick} name = {name}>Guardar</button>
    </div>
  );
}

export default App;
