import React, {useState} from 'react';
import './App.css';

//localStorage.setItem('name', JSON.stringify(this.state))

function App() {
  const [name, setName] = useState('Tiliin');

  const handleOnClick = (nombreInput) => {
    setName(nombreInput.value);
  };

  const input = (event) => {
    let input = event.target;
    console.log(input.value);
    setName(input.value)
  }

  //localStorage.setItem('name', JSON.stringify(name))

  return (
    <div>
      <p>Esooo {name}!!</p>
      <input id="nombreInput" type="text" onInput={input}></input><button type = "button" onClick={handleOnClick}>Cambiar y guardar</button>
    </div>
  );
}

export default App;
